df = pd.DataFrame(cars_data)

st.sidebar.title("🚗 차량 필터")
selected_brand = st.sidebar.selectbox("브랜드 선택", options=list(brands_models.keys()))
selected_category = st.sidebar.selectbox("차종 선택", options=list(brands_models[selected_brand].keys()))
selected_models = brands_models[selected_brand][selected_category]
selected_model = st.sidebar.selectbox("모델 선택", options=selected_models)

# 필터링
filtered_df = df[
    (df['brand'] == selected_brand) &
    (df['category'] == selected_category) &
    (df['model'] == selected_model)
]

# 결과 출력
st.title("🚘 차량 검색 결과")

if not filtered_df.empty:
    for _, row in filtered_df.iterrows():
        st.subheader(f"{row['year']} {row['brand']} {row['model']}")
        st.image(row["image"], use_column_width=True)
        st.write(f"연식: {row['year']}년 | 주행거리: {row['mileage']}km | 가격: {row['price']}만원")
        st.markdown(f"[차량 상세보기]({row['link']})")
else:
    st.warning("선택하신 조건에 맞는 차량이 없습니다.")